document.addEventListener('DOMContentLoaded', () => {
    const timeElement = document.getElementById('time');
    const totalTime = 30; // 30 minutes
    const colorChangeTime = 29;
    let remainingTime = totalTime;

    function updateTimer() {
        let minutes = Math.floor(remainingTime);
        
        // Update the display to show only minutes
        timeElement.textContent = `${minutes}`;
        remainingTime -= 1/60; // Decrease by 1 second

        if (totalTime <= colorChangeTime) {
            timerElement.style.borderColor = '#e74c3c'; // 色を赤に変更
        }

    }

    // Update every second
    const timerInterval = setInterval(updateTimer, 1000);

    // Initial call to display the starting time
    updateTimer();
});
// switch.js
window.onload = function() {
    const switchInterval = 5000; // 20秒（20000ミリ秒）

    function switchPage() {
        // 現在のURLのパスを取得
        const currentPath = window.location.pathname;

        // 次のページのURLを決定
        let nextPage;
        if (currentPath.includes('page1.html')) {
            nextPage = 'index_akai.html'; // 現在がpage1.htmlならpage2.htmlに遷移
        } else {
            nextPage = 'index_nemoto.html'; // それ以外ならpage1.htmlに遷移
        }

        // ページを切り替え
        window.location.href = nextPage;
    }

    // ページ切り替えのタイマー設定
    setInterval(switchPage, switchInterval);
};

